import React, { useEffect } from 'react';
import { Typography, Box, Container, Grid, Card, CardActionArea, CardMedia, CardHeader, Paper, Button } from '@material-ui/core';
import { ArrowRightAlt } from '@material-ui/icons';
import service from '../services/ProjectService';
import theme from '../theme/theme';
import { makeStyles } from '@material-ui/core/styles';
import { Link } from 'react-router-dom';
import Carousel from 'react-material-ui-carousel'
import MediaQuery from 'react-responsive';

/**
 * MyPortfolio
 * @Author Holland Aucoin
 * 
 * Introduction Projects
 * @Summary This component displays the introduction to projects that is shown on the home page
 */

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: theme.palette.background.paper,
    },
    tabs: {
        minWidth: 50,
        ['@media (min-width:600px)']: { // eslint-disable-line no-useless-computed-key
            minWidth: 50
        }
    },
}));

export default function App() {

    const classes = useStyles();

    // Creating variable of project list to be filled be appropriate service call
    const [projects, setProjects] = React.useState([]);

    // Use useEffect method to call backend to get the projects and wait for response
    useEffect(() => {
        service.getProjects().then(response => {
            setProjects(response.data);
        })
    }, []);

    // Iterate through an array of projects and turn it into its own array based on technology
    function getProjectOfLanguage(language) {
        let projectsByLanguage = [];

        projects.map((project, index) => (

            project.languages.includes(language) ? projectsByLanguage[index] = project : null
        ))

        return projectsByLanguage;
    }

    // Set array of projects based on language by calling method
    let javaProjects = getProjectOfLanguage("Java");
    let cProjects = getProjectOfLanguage("C#");
    let phpProjects = getProjectOfLanguage("PHP");
    let javascriptProjects = getProjectOfLanguage("JavaScript");

    return (

        <section>
            <Paper elevation={3}>
                <Box pt={6} pb={4}>
                    <div align="center" style={{paddingBottom: "25px"}}>
                        <Typography variant="h5" gutterBottom={true}>Projects</Typography>
                        <hr width="100px" align="center"/>
                    </div>
                    <Container maxWidth="md">
                        <Box textAlign="center">
                            <Grid container spacing={3}>

                            <MediaQuery maxWidth={theme.breakpoints.values.iPad}>
                                <Carousel autoPlay={false} swipe={true} animation="slide">
                                    {/* Iterate through the design descriptions */}
                                    { projects.map((project, index) => 
                                        <Grid item key={index} style={{padding: 20}}>
                                            <Card>
                                                <Link to={{ pathname: '/project', state: { projectId: project._id } }} style={{ textDecoration: 'none', color: 'black' }}>
                                                    <CardActionArea>
                                                        <CardMedia component="img" className={classes.media} image={project.coverImage} />
                                                        <CardHeader style={{height: 40}} subheader={project.name} sub titleTypographyProps={{ gutterBottom: false }} />
                                                    </CardActionArea>
                                                </Link>
                                            </Card>
                                        </Grid>
                                    )}
                                </Carousel>
                            </MediaQuery>

                            <MediaQuery minWidth={theme.breakpoints.values.iPad + 1}>
                                <Carousel autoPlay={false} swipe={true} animation="slide">
                                    {/* Iterate through the design descriptions */}
                                    { projects.map((project, index) => 
                                        <Box style={{display: "flex"}}>
                                         {(() => { 
                                            if(index * 2 < projects.length)
                                            index = index * 2;
                                        })()}
                                        <Grid item sm={6} md={6} key={index} style={{padding: 20}}>
                                            <Card>
                                                <Link to={{ pathname: '/project', state: { projectId: project._id } }} style={{ textDecoration: 'none', color: 'black' }}>
                                                    <CardActionArea>
                                                        <CardMedia component="img" className={classes.media} image={project.coverImage} />
                                                        <CardHeader style={{height: 40}} subheader={project.name} sub titleTypographyProps={{ gutterBottom: false }} />
                                                    </CardActionArea>
                                                </Link>
                                            </Card>
                                        </Grid>
                                        {(() => { 
                                            if(index + 1 < projects.length)
                                            project = projects[index + 1];
                                        })()}
                                        <Grid item sm={6} md={6} key={index} style={{padding: 20}}>
                                            <Card>
                                                <Link to={{ pathname: '/project', state: { projectId: project._id } }} style={{ textDecoration: 'none', color: 'black' }}>
                                                    <CardActionArea>
                                                        <CardMedia component="img" className={classes.media} image={project.coverImage} />
                                                        <CardHeader style={{height: 40}} subheader={project.name} sub titleTypographyProps={{ gutterBottom: false }} />
                                                    </CardActionArea>
                                                </Link>
                                            </Card>
                                        </Grid>
                                        </Box>
                                    )}
                                </Carousel>
                            </MediaQuery>

                            </Grid>
                        </Box>
                    </Container>
                </Box>
                <Box pb={6} textAlign="center">
                    <Button href="/projects" color="primary" endIcon={<ArrowRightAlt />}>View All</Button>
                </Box>
            </Paper>
        </section>

    )
}
